package test;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name1="sweta";
		String name2="sneha anu";
		System.out.println(" string length in 1st string is "+ name1.length());
		System.out.println(" string length in 1st string is "+ name2.length());
		System.out.println(" character in 1st string is "+ name1.charAt(3));
		System.out.println(" character in 1st string is "+ name1.charAt(name1.length()-1));
		System.out.println(" string length in 1st string is "+ name1.toUpperCase());
	}

}
